import {
    useEffect,
    useRef
} from 'react';
/**
 * Return the previous input value.
 *
 * @param value - Value to monitor for changes
 * @returns the previous value if it has changed otherwise returns undefined
 */
export default (function(value) {
    var ref = useRef();
    useEffect(function() {
        ref.current = value;
    }, [value]);
    return ref.current;
});